import { useEffect, useState } from "react";
import { Api } from "../../services/connection";
import { Image } from "react-bootstrap";
import "./posts.css";

const Posts = () => {

  const [data, setData] = useState(undefined);
  const getPosts = async () => {
    try{
      const res = await Api.get('/activities/posts');
      setData(res.data.data);
    }catch(e){}
  };

  useEffect(()=> {
    getPosts();
  }, []);

  return (
  data !== undefined ?
  <div className="content">
    <div className="post-item">
      {data.map((e)=> (
        <div key={e.id} className="content-post">
          <a href="/teste">
            <div className="title" >{e.data.title}</div>
          </a>
          <div className="description">
            <div> {e.data.smallDescription}</div>
            <img src={`https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.healio.com%2Fnews%2Fpsychiatry%2F20200506%2Fenvironmental-factors-fail-to-explain-increased-asd-prevalence&psig=AOvVaw34ZOsWQNOH8VdUIMhL24ug&ust=1615006828631000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCMjh2bSvmO8CFQAAAAAdAAAAABAD`} />
          </div>
        </div>
      ))}  
    </div>
  </div>: <div className="content"/>
  )
}
export {Posts}
