import { useEffect, useState } from "react";
import { useHistory, useLocation } from "react-router-dom";
import { Api } from "../../services/connection";
import { Image } from "react-bootstrap";
import './post.css';

const Post = () => { 
  const history = useHistory();
  const useQuery = () => new URLSearchParams(useLocation().search);
  const id = useQuery().get("id");
  const [data, setData] = useState();

  const getPost = async (idPost) => {
    try{
      const res = await Api.get(`/activities/post${idPost}`);
      setData(res.data.data);
    }catch(e){
      console.log(e);
    }
  } 

  useEffect(()=> {
    const idPost = id;
    if (!idPost) history.push('/');
    else getPost(idPost);
  }, []);

  return (
    data ?
  <div className="content">
    <div className="itens">
      <img className="image-title" src={`${data.image}`}/>
      <h1>{data.title}</h1>
      <h3>{data.smallDescription}</h3>
      <div className="materia">
        {data.fullDescription.map((e, index)=> <p key={`${index}`}>{e}</p>)}
      </div>
    </div>
  </div> : <div/>)
}
export {Post};
